package DataTypes;

public class Loops {

	public static void main(String[] args) {
		// FIXME Auto-generated method stub
		
		//While Loop
		int i=1;
		while (i<=10) {
			System.out.println(i); //1 to 10
			i=i+1;
		}
		
		System.out.println("**********************************************");
		
		//For Loop
		for(int k=5; k<=10;k++) {
			System.out.println(k);
		}
	}

}
